using System;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create HTML structure\n        XDocument htmlDoc = new XDocument(\n            new XElement("html",\n                new XElement("head",\n                    new XElement("title", "Sample Page")\n                ),\n                new XElement("body",\n                    new XElement("h1", "Hello, World!"),\n                    new XElement("p", "This is a sample HTML page created programmatically.")\n                )\n            )\n        );\n\n        // Save the HTML document to a file\n        string filePath = "sample.html";\n        htmlDoc.Save(filePath);\n\n        Console.WriteLine($"HTML file created successfully at {filePath}");\n    }\n}