public class MyService : IMyService\n{\n    public XmlElement GetXmlData()\n    {\n        // Assume you have a DataTable 'dataTable' filled with your data\n        DataTable dataTable = GetDataTable(); // Your method to get the DataTable\n\n        // Convert DataTable to XML\n        DataSet dataSet = new DataSet();\n        dataSet.Tables.Add(dataTable);\n        string xmlString = dataSet.GetXml();\n\n        // Load the XML string into an XmlDocument\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(xmlString);\n\n        // Return the XML data as XmlElement\n        return xmlDoc.DocumentElement;\n    }\n\n    private DataTable GetDataTable()\n    {\n        // Your implementation to fetch data and fill the DataTable\n        DataTable dt = new DataTable();\n        // Fill the DataTable with your data\n        return dt;\n    }\n}