string sql = "SELECT [WallPost], [DatePosted] FROM [WallTable] WHERE [UserId] = @UserId";\n\nstring strCon = System.Web.Configuration.WebConfigurationManager.ConnectionStrings["SocialSiteConnectionString"].ConnectionString;\n\nusing (SqlConnection conn = new SqlConnection(strCon))\n{\n    SqlDataAdapter daUserProfile = new SqlDataAdapter(sql, conn);\n    daUserProfile.SelectCommand.Parameters.AddWithValue("@UserId", Request["friend"]);\n\n    DataTable dtUserProfile = new DataTable();\n    daUserProfile.Fill(dtUserProfile);\n\n    rpWall2.DataSource = dtUserProfile; // rpWall2 = repeater control\n    rpWall2.DataBind();\n}