using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a dictionary with string keys and int values\n        Dictionary<string, int> dictionary = new Dictionary<string, int>();\n\n        // Add key/value pairs to the dictionary\n        dictionary.Add("one", 1);\n        dictionary.Add("two", 2);\n        dictionary.Add("three", 3);\n\n        // Access a value by key\n        int value = dictionary["two"];\n        Console.WriteLine($"Value for 'two': {value}");\n\n        // Check if a key exists\n        bool containsKey = dictionary.ContainsKey("three");\n        Console.WriteLine($"Contains key 'three': {containsKey}");\n\n        // Remove a key/value pair\n        dictionary.Remove("one");\n    }\n}