using System;\nusing System.IO;\nusing System.Web;\n\npublic class RequestLoggingModule : IHttpModule\n{\n    public void Init(HttpApplication context)\n    {\n        context.BeginRequest += new EventHandler(OnBeginRequest);\n    }\n\n    private void OnBeginRequest(object sender, EventArgs e)\n    {\n        HttpApplication application = (HttpApplication)sender;\n        HttpContext context = application.Context;\n\n        // Log request headers\n        string headers = context.Request.Headers.ToString();\n        File.AppendAllText(@"C:\path\to\log\headers.log", headers + Environment.NewLine);\n\n        // Log request body\n        context.Request.InputStream.Position = 0;\n        using (StreamReader reader = new StreamReader(context.Request.InputStream))\n        {\n            string body = reader.ReadToEnd();\n            File.AppendAllText(@"C:\path\to\log\body.log", body + Environment.NewLine);\n        }\n    }\n\n    public void Dispose() { }\n}