using System;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            MailAddress address = new MailAddress("John Doe <john.doe@example.com>");\n            Console.WriteLine("Address: " + address.Address);\n            Console.WriteLine("Display Name: " + address.DisplayName);\n        }\n        catch (FormatException ex)\n        {\n            Console.WriteLine("Invalid email address format: " + ex.Message);\n        }\n    }\n}