[Route("api/combined")]\npublic class CombinedController : ApiController\n{\n    private YourDbContext db = new YourDbContext();\n\n    // GET: api/combined\n    [HttpGet]\n    public IHttpActionResult GetCombinedData()\n    {\n        var combinedData = from header in db.Headers\n                           join detail in db.Details\n                           on header.Id equals detail.HeaderId\n                           select new\n                           {\n                               Header = header,\n                               Detail = detail\n                           };\n\n        return Ok(combinedData.ToList());\n    }\n}