using Oracle.DataAccess.Client;\n\npublic void DeriveOracleParameters()\n{\n    string connectionString = "your_connection_string_here";\n    string storedProcedureName = "your_stored_procedure_name_here";\n\n    using (OracleConnection connection = new OracleConnection(connectionString))\n    {\n        connection.Open();\n        using (OracleCommand command = new OracleCommand(storedProcedureName, connection))\n        {\n            command.CommandType = System.Data.CommandType.StoredProcedure;\n\n            // Derive parameters\n            OracleCommandBuilder.DeriveParameters(command);\n\n            // Now command.Parameters contains the derived parameters\n            foreach (OracleParameter parameter in command.Parameters)\n            {\n                Console.WriteLine("Parameter: " + parameter.ParameterName + " Type: " + parameter.OracleDbType);\n            }\n        }\n    }\n}