public void CopyDefaultValuesToGameTables(int gameId)\n{\n    using (var context = new YourDataContext())\n    {\n        // Retrieve default data\n        var defaultRegions = context.DefaultRegions.ToList();\n        var defaultSubRegions = context.DefaultSubRegions.ToList();\n        var defaultCountries = context.DefaultCountries.ToList();\n\n        // Create dictionaries to map old IDs to new entities\n        var regionMap = new Dictionary<int, Region>();\n        var subRegionMap = new Dictionary<int, SubRegion>();\n\n        // Copy regions\n        foreach (var defaultRegion in defaultRegions)\n        {\n            var newRegion = new Region\n            {\n                GameId = gameId,\n                Name = defaultRegion.Name\n                // Copy other properties as needed\n            };\n            context.Regions.InsertOnSubmit(newRegion);\n            context.SubmitChanges(); // Submit to get the new ID\n            regionMap[defaultRegion.Id] = newRegion;\n        }\n\n        // Copy subregions\n        foreach (var defaultSubRegion in defaultSubRegions)\n        {\n            var newSubRegion = new SubRegion\n            {\n                GameId = gameId,\n                Name = defaultSubRegion.Name,\n                RegionId = regionMap[defaultSubRegion.RegionId].Id\n                // Copy other properties as needed\n            };\n            context.SubRegions.InsertOnSubmit(newSubRegion);\n            context.SubmitChanges(); // Submit to get the new ID\n            subRegionMap[defaultSubRegion.Id] = newSubRegion;\n        }\n\n        // Copy countries\n        foreach (var defaultCountry in defaultCountries)\n        {\n            var newCountry = new Country\n            {\n                GameId = gameId,\n                Name = defaultCountry.Name,\n                SubRegionId = subRegionMap[defaultCountry.SubRegionId].Id\n                // Copy other properties as needed\n            };\n            context.Countries.InsertOnSubmit(newCountry);\n        }\n\n        // Submit all country inserts in one go\n        context.SubmitChanges();\n    }\n}