using System;\nusing System.Text;\nusing System.Security.Cryptography;\n\npublic class KeyManager\n{\n    public static void StoreKey(string key, string filePath)\n    {\n        byte[] keyBytes = Encoding.UTF8.GetBytes(key);\n        byte[] encryptedKey = ProtectedData.Protect(keyBytes, null, DataProtectionScope.CurrentUser);\n        System.IO.File.WriteAllBytes(filePath, encryptedKey);\n    }\n\n    public static string RetrieveKey(string filePath)\n    {\n        byte[] encryptedKey = System.IO.File.ReadAllBytes(filePath);\n        byte[] decryptedKey = ProtectedData.Unprotect(encryptedKey, null, DataProtectionScope.CurrentUser);\n        return Encoding.UTF8.GetString(decryptedKey);\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string key = "my-secure-key-12345678";\n        string filePath = "keyfile.dat";\n\n        // Store the key\n        KeyManager.StoreKey(key, filePath);\n\n        // Retrieve the key\n        string retrievedKey = KeyManager.RetrieveKey(filePath);\n\n        Console.WriteLine($"Retrieved Key: {retrievedKey}");\n    }\n}