using System.Diagnostics;\n\n// Create and start the process\nProcess P1 = new Process();\nP1.StartInfo.FileName = "myexe.exe";\nP1.Start();\n\n// Code to measure run-time or other operations\n\n// In the cancel button's event handler\nprivate void CancelButton_Click(object sender, EventArgs e)\n{\n    if (!P1.HasExited)\n    {\n        P1.Kill(); // Terminate the process\n        P1.WaitForExit(); // Optionally wait for the process to exit\n    }\n    P1.Close(); // Release resources\n}