using System;\nusing System.IO;\nusing System.Linq;\nusing System.Reflection;\nusing System.Windows.Forms;\n\npublic partial class MainForm : Form\n{\n    public MainForm()\n    {\n        InitializeComponent();\n        LoadModules();\n    }\n\n    private void LoadModules()\n    {\n        string modulesPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "Modules");\n        if (!Directory.Exists(modulesPath))\n        {\n            Directory.CreateDirectory(modulesPath);\n        }\n\n        foreach (string file in Directory.GetFiles(modulesPath, "*.dll"))\n        {\n            Assembly assembly = Assembly.LoadFrom(file);\n            var moduleTypes = assembly.GetTypes().Where(t => typeof(IModule).IsAssignableFrom(t) && !t.IsInterface);\n\n            foreach (var type in moduleTypes)\n            {\n                var module = (IModule)Activator.CreateInstance(type);\n                TabPage tabPage = new TabPage(module.Name);\n                tabPage.Controls.Add(module.GetControl());\n                tabControl1.TabPages.Add(tabPage);\n            }\n        }\n    }\n}