private void CopyGraphics()\n{\n    // Create a bitmap with the same size as the source panel\n    Bitmap srcBmp = new Bitmap(pnl.Width, pnl.Height);\n    \n    // Create Graphics objects from the bitmap and the source panel\n    Graphics srcPic = pnl.CreateGraphics();\n    Graphics srcMem = Graphics.FromImage(srcBmp);\n    \n    // Copy the contents of the source panel's Graphics object to the bitmap's Graphics object\n    srcMem.DrawImage(srcBmp, 0, 0);\n    \n    // Set the copied image as the background of the destination panel\n    pnlDraw.BackgroundImage = srcBmp;\n\n    // Clean up\n    srcPic.Dispose();\n    srcMem.Dispose();\n}