public static class ContextInitializer\n{\n    static ContextInitializer()\n    {\n        foreach (Type type in System.Reflection.Assembly.GetExecutingAssembly().GetTypes())\n        {\n            if (type.GetCustomAttributes(typeof(System.Data.Linq.Mapping.TableAttribute), true).Length > 0)\n            {\n                System.ComponentModel.TypeDescriptor.AddProvider(\n                    new Hyper.ComponentModel.HyperTypeDescriptionProvider(\n                        System.ComponentModel.TypeDescriptor.GetProvider(type)), \n                    type);\n            }\n        }\n    }\n}