using System;\nusing System.Windows;\nusing System.Windows.Navigation;\nusing System.Reflection;\n\npublic class PageNavigator\n{\n    public static void NavigateToPage(string pagePath, NavigationService navigationService)\n    {\n        // Convert the path to a class name\n        string className = ConvertPathToClassName(pagePath);\n\n        // Get the type of the class\n        Type pageType = Type.GetType(className);\n\n        if (pageType != null)\n        {\n            // Create an instance of the page\n            object pageInstance = Activator.CreateInstance(pageType);\n\n            // Navigate to the page\n            navigationService.Navigate(pageInstance);\n        }\n        else\n        {\n            throw new Exception("Page type not found");\n        }\n    }\n\n    private static string ConvertPathToClassName(string pagePath)\n    {\n        // Remove the file extension and convert to namespace format\n        string className = pagePath.Replace("\\", ".").Replace("/", ".").Replace(".xaml", "");\n        \n        // Assuming pages are under the namespace "YourAppNamespace.Pages"\n        return $"YourAppNamespace{className}";\n    }\n}