using System;\nusing System.IO;\nusing System.Net.Sockets;\nusing System.Text;\n\npublic class IcapClient\n{\n    private const string IcapServer = "icap-server-address";\n    private const int IcapPort = 1344;\n    private const string IcapService = "avscan";\n\n    public static void ScanFile(string filePath)\n    {\n        byte[] fileBytes = File.ReadAllBytes(filePath);\n        string fileName = Path.GetFileName(filePath);\n        \n        using (TcpClient client = new TcpClient(IcapServer, IcapPort))\n        using (NetworkStream stream = client.GetStream())\n        {\n            string httpRequest = $"POST /{IcapService} HTTP/1.1\r\n" +\n                                 $"Host: {IcapServer}\r\n" +\n                                 $"User-Agent: IcapClient\r\n" +\n                                 $"Content-Length: {fileBytes.Length}\r\n" +\n                                 $"Content-Type: application/octet-stream\r\n\r\n";\n\n            byte[] requestBytes = Encoding.ASCII.GetBytes(httpRequest);\n            stream.Write(requestBytes, 0, requestBytes.Length);\n            stream.Write(fileBytes, 0, fileBytes.Length);\n            \n            // Read the response (for demonstration purposes, this is simplified)\n            using (StreamReader reader = new StreamReader(stream, Encoding.ASCII))\n            {\n                string response = reader.ReadToEnd();\n                Console.WriteLine("Response from ICAP server:");\n                Console.WriteLine(response);\n            }\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string filePath = "path-to-your-file";\n        ScanFile(filePath);\n    }\n}