protected void btnSearch_Click(object sender, EventArgs e)\n{\n    Dictionary<string, string> parameters = new Dictionary<string, string>();\n\n    // Assuming you have ListBoxes and DropDownLists with IDs listBox1, dropDownList1, etc.\n    if (!string.IsNullOrEmpty(listBox1.SelectedValue))\n    {\n        parameters.Add("Column1", listBox1.SelectedValue);\n    }\n    if (!string.IsNullOrEmpty(dropDownList1.SelectedValue))\n    {\n        parameters.Add("Column2", dropDownList1.SelectedValue);\n    }\n\n    QueryBuilder qb = new QueryBuilder();\n    string query = qb.BuildQuery(parameters);\n\n    // Bind the query to the DataGrid\n    BindData(query, parameters);\n}\n\nprivate void BindData(string query, Dictionary<string, string> parameters)\n{\n    using (OracleConnection conn = new OracleConnection("Your Connection String"))\n    {\n        using (OracleCommand cmd = new OracleCommand(query, conn))\n        {\n            foreach (var param in parameters)\n            {\n                cmd.Parameters.Add(new OracleParameter($":{param.Key}", param.Value));\n            }\n\n            conn.Open();\n            OracleDataReader reader = cmd.ExecuteReader();\n            yourDataGrid.DataSource = reader;\n            yourDataGrid.DataBind();\n        }\n    }\n}