using NPOI.SS.UserModel;\n   using NPOI.XSSF.UserModel; // for .xlsx\n   using NPOI.HSSF.UserModel; // for .xls\n   using System;\n   using System.IO;\n\n   class Program\n   {\n       static void Main()\n       {\n           using (FileStream file = new FileStream(@"path\to\your\file.xlsx", FileMode.Open, FileAccess.Read))\n           {\n               IWorkbook workbook = new XSSFWorkbook(file); // for .xlsx\n               // IWorkbook workbook = new HSSFWorkbook(file); // for .xls\n               ISheet sheet = workbook.GetSheetAt(0);\n               for (int row = 0; row <= sheet.LastRowNum; row++)\n               {\n                   IRow rowObj = sheet.GetRow(row);\n                   for (int col = 0; col < rowObj.LastCellNum; col++)\n                   {\n                       Console.WriteLine(rowObj.GetCell(col).ToString());\n                   }\n               }\n           }\n       }\n   }