public class MainPage : UserControl\n{\n    public MainPage()\n    {\n        InitializeComponent();\n        LoadData();\n    }\n\n    private void LoadData()\n    {\n        WebClient client = new WebClient();\n        client.DownloadStringCompleted += new DownloadStringCompletedEventHandler(OnDataDownloaded);\n        client.DownloadStringAsync(new Uri("http://your-service-url/GetData"));\n    }\n\n    private void OnDataDownloaded(object sender, DownloadStringCompletedEventArgs e)\n    {\n        if (e.Error == null)\n        {\n            string jsonData = e.Result;\n            var data = JsonConvert.DeserializeObject<IEnumerable<dynamic>>(jsonData);\n            dataGrid.ItemsSource = data;\n        }\n        else\n        {\n            // Handle error\n        }\n    }\n}