[ServiceContract]\npublic interface IDataService\n{\n    [OperationContract]\n    [WebGet(ResponseFormat = WebMessageFormat.Json)]\n    string GetData();  // This method returns data in JSON format\n}\n\npublic class DataService : IDataService\n{\n    public string GetData()\n    {\n        // Fetch data from database and serialize to JSON\n        // For example purposes, let's assume we return a JSON string\n        var dataTable = GetDataTableFromDatabase();\n        string jsonResult = JsonConvert.SerializeObject(dataTable);\n        return jsonResult;\n    }\n\n    private DataTable GetDataTableFromDatabase()\n    {\n        // Implement your database fetch logic here\n        DataTable dataTable = new DataTable();\n        // Populate the dataTable\n        return dataTable;\n    }\n}