using System;\nusing System.Text.RegularExpressions;\n\npublic class Program\n{\n    public static void Main()\n    {\n        Func<string, bool> RegexValidate = s => Regex.IsMatch(s, @"^(?:\D*\d){7,}.*$");\n\n        string x = "asda 1234567 sdfasdf";\n        string y = "asda   sdfa 123456 sdfasdf";\n\n        bool xx = RegexValidate(x); // true\n        bool yy = RegexValidate(y); // false\n\n        Console.WriteLine(xx); // Output: True\n        Console.WriteLine(yy); // Output: False\n    }\n}