using HtmlAgilityPack;\nusing System;\n\npublic class HtmlProcessor\n{\n    public string ProcessHtml(string source, string tag, string attribute)\n    {\n        var htmlDoc = new HtmlDocument();\n        htmlDoc.LoadHtml(source);\n\n        var nodes = htmlDoc.DocumentNode.SelectNodes($"//{tag}[@{attribute}]");\n        if (nodes != null)\n        {\n            foreach (var node in nodes)\n            {\n                node.Attributes.Remove(attribute);\n            }\n        }\n\n        return htmlDoc.DocumentNode.OuterHtml;\n    }\n\n    public static void Main()\n    {\n        string before = @"<input type=""text"" name=""Input"" id=""Input"" onMouseOver=""some js to be eliminated"">";\n        var processor = new HtmlProcessor();\n        string after = processor.ProcessHtml(before, "input", "onMouseOver");\n        Console.WriteLine(after);  // Output: <input type="text" name="Input" id="Input">\n    }\n}