var usersWithoutTerritories = from u in db.Users\n                              join uXt in db.User_x_Territories on u.Id equals uXt.UserID into userTerritories\n                              from ut in userTerritories.DefaultIfEmpty()\n                              group ut by new { u.Id, u.FirstName, u.LastName } into g\n                              where g.Count(ut => ut != null) == 0\n                              select new\n                              {\n                                  Id = g.Key.Id,\n                                  FirstName = g.Key.FirstName,\n                                  LastName = g.Key.LastName\n                              };\n\nforeach (var user in usersWithoutTerritories)\n{\n    Console.WriteLine($"Id: {user.Id}, FirstName: {user.FirstName}, LastName: {user.LastName}");\n}