using System;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        string url = "http://www.mysite.com";\n        using (WebClient webClient = new WebClient())\n        {\n            // Use default system credentials\n            webClient.UseDefaultCredentials = true;\n\n            try\n            {\n                byte[] data = webClient.DownloadData(url);\n                Console.WriteLine("Download successful.");\n            }\n            catch (WebException ex)\n            {\n                if (ex.Response is HttpWebResponse response && response.StatusCode == HttpStatusCode.ProxyAuthenticationRequired)\n                {\n                    Console.WriteLine("Proxy authentication required. Please check your proxy settings.");\n                    // Redirect user to help page or handle accordingly\n                }\n                else\n                {\n                    Console.WriteLine("An error occurred: " + ex.Message);\n                }\n            }\n        }\n    }\n}