using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\nusing System.Threading;\nusing System.Windows.Forms;\n\nclass Program\n{\n    [DllImport("user32.dll")]\n    private static extern IntPtr FindWindow(string lpClassName, string lpWindowName);\n\n    [DllImport("user32.dll")]\n    private static extern bool SendMessage(IntPtr hWnd, int Msg, IntPtr wParam, IntPtr lParam);\n\n    private const int WM_SHOWFORM = 0x0401;\n\n    static Mutex mutex = new Mutex(true, "{Your-GUID-Here}");\n\n    [STAThread]\n    static void Main()\n    {\n        if (mutex.WaitOne(TimeSpan.Zero, true))\n        {\n            Application.Run(new YourMainForm());\n            mutex.ReleaseMutex();\n        }\n        else\n        {\n            IntPtr hWnd = FindWindow(null, "YourMainFormTitle");\n            if (hWnd != IntPtr.Zero)\n            {\n                SendMessage(hWnd, WM_SHOWFORM, IntPtr.Zero, IntPtr.Zero);\n            }\n        }\n    }\n}