using System;\nusing System.Collections.Generic;\n\npublic class MonoCompatibilityChecker\n{\n    private static readonly Dictionary<string, string> MonoIssues = new Dictionary<string, string>\n    {\n        { "System.Drawing", "Limited support; consider using cross-platform libraries like SkiaSharp." },\n        { "System.Management", "Not supported; WMI is Windows-specific." },\n        { "System.ServiceModel", "Limited support for WCF; consider using gRPC or other alternatives." },\n        // Add more entries as needed\n    };\n\n    public static string CheckFunction(string functionName)\n    {\n        if (MonoIssues.TryGetValue(functionName, out string issue))\n        {\n            return $"Warning: {issue}";\n        }\n        return "No known issues with Mono.";\n    }\n}\n\n// Example usage\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        string functionName = "System.Drawing";\n        string message = MonoCompatibilityChecker.CheckFunction(functionName);\n        Console.WriteLine(message);\n    }\n}