var data = \nfrom details in h_details.Descendants("ROW")\njoin inst in instance.XPathSelectElements("//Row")\non new \n{ \n    ID = details.Element("ID").Value, \n    LANGUAGE = details.Element("LANGUAGE").Value \n}\nequals new \n{\n    ID = inst.XPathSelectElement("Field[@Name='h_id']").Value, \n    LANGUAGE = inst.XPathSelectElement("Field[@Name='h_lang']").Value \n}\nselect new\n{\n    Details = details,\n    Instance = inst\n};