using System;\nusing System.Data;\nusing System.IO;\nusing System.Text;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string itemsXML = @"\n        <items>\n          <item>\n            <id>i1</id>\n            <name>item1</name>\n            <subitems>\n              <name>subitem1</name>\n              <name>subitem2</name>\n            </subitems>\n          </item>\n          <item>\n            <id>i2</id>\n            <name>item2</name>\n            <subitems>\n              <name>subitem1</name>\n              <name>subitem2</name>\n            </subitems>\n          </item>\n        </items>";\n\n        MemoryStream itemsStream = new MemoryStream(Encoding.ASCII.GetBytes(itemsXML));\n        DataSet itemsSet = new DataSet();\n        itemsSet.ReadXml(itemsStream);\n\n        foreach (DataRow itemRow in itemsSet.Tables["item"].Rows)\n        {\n            Console.WriteLine("Item ID: " + itemRow["id"] + ", Name: " + itemRow["name"]);\n            foreach (DataRow subitemRow in itemRow.GetChildRows("item_subitems"))\n            {\n                Console.WriteLine("  Subitem Name: " + subitemRow["name"]);\n            }\n        }\n    }\n}