using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xml = @"<env:Envelope\n            xmlns:env='http://schemas.xmlsoap.org/soap/envelope/'\n            xmlns:xsd='http://www.w3.org/2001/XMLSchema'\n            xmlns:xsi='http://www.w3.org/2001/XMLSchema-instance'\n            xmlns:ns0='http://dev1/MyWebService1.wsdl'>\n            <env:Header>\n                <wsse:Security\n                    xmlns:wsse='http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-secext-1.0.xsd'\n                    xmlns='http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-secext-1.0.xsd'\n                    xmlns:env='http://schemas.xmlsoap.org/soap/envelope/'\n                    env:mustUnderstand='1'/>\n            </env:Header>\n            <env:Body>\n                <ns0:getItemNbrByItemIdResponseElement>\n                    <ns0:result>1010603</ns0:result>\n                </ns0:getItemNbrByItemIdResponseElement>\n            </env:Body>\n        </env:Envelope>";\n\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xml);\n\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(doc.NameTable);\n        nsmgr.AddNamespace("env", "http://schemas.xmlsoap.org/soap/envelope/");\n        nsmgr.AddNamespace("ns0", "http://dev1/MyWebService1.wsdl");\n\n        XmlNode resultNode = doc.SelectSingleNode("//ns0:result", nsmgr);\n\n        if (resultNode != null)\n        {\n            string resultValue = resultNode.InnerText;\n            Console.WriteLine("Item Number: " + resultValue);\n        }\n        else\n        {\n            Console.WriteLine("Result not found.");\n        }\n    }\n}