// Server code\nsslStream.AuthenticateAsServer(serverCertificate, clientCertificateRequired: true, checkCertificateRevocation: true);\n\n// Validation callback to debug issues\nsslStream.RemoteCertificateValidationCallback = (sender, certificate, chain, sslPolicyErrors) =>\n{\n    if (sslPolicyErrors == SslPolicyErrors.None)\n    {\n        return true;\n    }\n    Console.WriteLine("Certificate error: {0}", sslPolicyErrors);\n    return false;\n};