// Client code\nX509Certificate2 clientCertificate = new X509Certificate2("path_to_client_certificate.pfx", "password");\n\nsslStream.AuthenticateAsClient(serverName, new X509CertificateCollection { clientCertificate }, SslProtocols.Tls12, checkCertificateRevocation: true);\n\n// Client certificate selection callback\nsslStream.ClientCertificateSelectionCallback = (sender, targetHost, localCertificates, remoteCertificate, acceptableIssuers) =>\n{\n    // Return the client certificate\n    return clientCertificate;\n};