using System;\nusing System.Data.SqlClient;\n\npublic class DatabaseOwner\n{\n    public static string GetDatabaseOwner(string connectionString, string databaseName)\n    {\n        string owner = null;\n        \n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            string query = "SELECT suser_sname(owner_sid) AS Owner FROM sys.databases WHERE name = @DatabaseName";\n            \n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                command.Parameters.AddWithValue("@DatabaseName", databaseName);\n                \n                connection.Open();\n                \n                SqlDataReader reader = command.ExecuteReader();\n                if (reader.Read())\n                {\n                    owner = reader["Owner"].ToString();\n                }\n            }\n        }\n        return owner;\n    }\n    \n    public static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string databaseName = "your_database_name_here";\n        string owner = GetDatabaseOwner(connectionString, databaseName);\n        \n        if (owner != null)\n        {\n            Console.WriteLine("The owner of the database is: " + owner);\n        }\n        else\n        {\n            Console.WriteLine("Database owner not found.");\n        }\n    }\n}