using System;\nusing System.Web;\nusing System.Configuration;\nusing System.Xml;\n\npublic class MvcApplication : System.Web.HttpApplication\n{\n    protected void Application_Start()\n    {\n        string environment = Environment.GetEnvironmentVariable("ASPNET_ENV");\n        string configFileName = $"web-{environment}.config";\n        string configFilePath = HttpContext.Current.Server.MapPath($"~/{configFileName}");\n\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load(configFilePath);\n\n        foreach (XmlNode node in xmlDoc.DocumentElement.ChildNodes)\n        {\n            if (node.Name == "appSettings")\n            {\n                foreach (XmlNode keyNode in node.ChildNodes)\n                {\n                    if (keyNode.Attributes["key"] != null && keyNode.Attributes["value"] != null)\n                    {\n                        ConfigurationManager.AppSettings.Set(\n                            keyNode.Attributes["key"].Value,\n                            keyNode.Attributes["value"].Value);\n                    }\n                }\n            }\n            // Add code for other sections if needed\n        }\n    }\n}