using System;\nusing System.Windows.Forms;\n\npublic class MultiScrollBarGridForm : Form\n{\n    private TableLayoutPanel tableLayoutPanel;\n    private DataGridView grid1, grid2, grid3;\n\n    public MultiScrollBarGridForm()\n    {\n        tableLayoutPanel = new TableLayoutPanel\n        {\n            Dock = DockStyle.Fill,\n            ColumnCount = 3,\n            RowCount = 1,\n            AutoSize = true\n        };\n\n        grid1 = new DataGridView { Dock = DockStyle.Fill, ColumnCount = 2 };\n        grid2 = new DataGridView { Dock = DockStyle.Fill, ColumnCount = 2 };\n        grid3 = new DataGridView { Dock = DockStyle.Fill, ColumnCount = 2 };\n\n        tableLayoutPanel.Controls.Add(grid1, 0, 0);\n        tableLayoutPanel.Controls.Add(grid2, 1, 0);\n        tableLayoutPanel.Controls.Add(grid3, 2, 0);\n\n        Controls.Add(tableLayoutPanel);\n\n        InitializeGrids();\n        SynchronizeScrolling();\n    }\n\n    private void InitializeGrids()\n    {\n        for (int i = 0; i < 10; i++)\n        {\n            grid1.Rows.Add($"G1R{i}C1", $"G1R{i}C2");\n            grid2.Rows.Add($"G2R{i}C1", $"G2R{i}C2");\n            grid3.Rows.Add($"G3R{i}C1", $"G3R{i}C2");\n        }\n    }\n\n    private void SynchronizeScrolling()\n    {\n        grid1.VerticalScrollBar.Scroll += (sender, e) => SynchronizeVerticalScroll(grid1, grid2, grid3);\n        grid2.VerticalScrollBar.Scroll += (sender, e) => SynchronizeVerticalScroll(grid2, grid1, grid3);\n        grid3.VerticalScrollBar.Scroll += (sender, e) => SynchronizeVerticalScroll(grid3, grid1, grid2);\n    }\n\n    private void SynchronizeVerticalScroll(DataGridView source, params DataGridView[] targets)\n    {\n        foreach (var target in targets)\n        {\n            target.FirstDisplayedScrollingRowIndex = source.FirstDisplayedScrollingRowIndex;\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MultiScrollBarGridForm());\n    }\n}