using NUnit.Framework;\n\npublic class Person\n{\n    private string _name = "John"; // Assign a default value for testing\n    private string _surname = string.Empty;\n\n    public string Name\n    {\n        get\n        {\n            return _name;\n        }\n    }\n\n    public string Surname\n    {\n        get\n        {\n            return _surname;\n        }\n        set\n        {\n            _surname = value;\n        }\n    }\n}\n\n[TestFixture]\npublic class PersonTests\n{\n    [Test]\n    public void NameProperty_ShouldReturnCorrectValue()\n    {\n        // Arrange\n        var person = new Person();\n\n        // Act\n        var name = person.Name;\n\n        // Assert\n        Assert.AreEqual("John", name);\n    }\n}