using System.Windows.Threading;\n\n// Initialize variables\nprivate DispatcherTimer movementTimer;\nprivate bool moveUp, moveDown, moveLeft, moveRight;\nprivate int gcX = 0, gcY = 0;\n\npublic MainWindow()\n{\n    InitializeComponent();\n\n    // Set up the movement timer\n    movementTimer = new DispatcherTimer();\n    movementTimer.Interval = TimeSpan.FromMilliseconds(16); // ~60 FPS\n    movementTimer.Tick += MovementTimer_Tick;\n    movementTimer.Start();\n}\n\nprivate void MovementTimer_Tick(object sender, EventArgs e)\n{\n    if (moveUp) gcY -= 5;\n    if (moveDown) gcY += 5;\n    if (moveLeft) gcX -= 5;\n    if (moveRight) gcX += 5;\n\n    gameCanvas.RenderTransform = new TranslateTransform(gcX, gcY);\n}\n\nprotected override void OnKeyDown(KeyEventArgs e)\n{\n    base.OnKeyDown(e);\n\n    if (e.Key == Key.W) moveUp = true;\n    if (e.Key == Key.S) moveDown = true;\n    if (e.Key == Key.A) moveLeft = true;\n    if (e.Key == Key.D) moveRight = true;\n}\n\nprotected override void OnKeyUp(KeyEventArgs e)\n{\n    base.OnKeyUp(e);\n\n    if (e.Key == Key.W) moveUp = false;\n    if (e.Key == Key.S) moveDown = false;\n    if (e.Key == Key.A) moveLeft = false;\n    if (e.Key == Key.D) moveRight = false;\n}