using DocumentFormat.OpenXml.Packaging;\n     using DocumentFormat.OpenXml.Wordprocessing;\n\n     public void FillWordForm(string sourceDocPath, string destinationDocPath)\n     {\n         File.Copy(sourceDocPath, destinationDocPath, true);\n         using (WordprocessingDocument wordDoc = WordprocessingDocument.Open(destinationDocPath, true))\n         {\n             var docText = wordDoc.MainDocumentPart.Document.InnerText;\n\n             // Replace placeholders with actual values\n             docText = docText.Replace("placeholder1", "value1");\n             docText = docText.Replace("placeholder2", "value2");\n\n             // Save changes to the document\n             wordDoc.MainDocumentPart.Document.Save();\n         }\n     }