using EnvDTE;\nusing EnvDTE80;\nusing Microsoft.VisualStudio.CommandBars;\nusing System;\nusing System.Text.RegularExpressions;\nusing System.Windows.Forms;\n\npublic class SwapExpressions\n{\n    public void Swap(DTE2 dte)\n    {\n        TextDocument textDoc = (TextDocument)dte.ActiveDocument.Object("TextDocument");\n        EditPoint startPoint = textDoc.StartPoint.CreateEditPoint();\n        EditPoint endPoint = textDoc.EndPoint.CreateEditPoint();\n        string text = startPoint.GetText(endPoint);\n\n        // Regular expression to match the pattern and swap sides\n        string pattern = @"(\w+)\s*=\s*(\w+)\s*;";\n        string replacement = "$2 = $1;";\n        string result = Regex.Replace(text, pattern, replacement);\n\n        // Replace the text in the document\n        startPoint.ReplaceText(endPoint, result, (int)vsEPReplaceTextOptions.vsEPReplaceTextAutoformat);\n    }\n}