using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class RegistryEncryption\n{\n    private static readonly string Key = "your-encryption-key"; // Must be 16, 24, or 32 bytes long for AES\n    private static readonly string IV = "your-encryption-iv"; // Must be 16 bytes long\n\n    public static string Encrypt(string plainText)\n    {\n        using (Aes aes = Aes.Create())\n        {\n            aes.Key = Encoding.UTF8.GetBytes(Key);\n            aes.IV = Encoding.UTF8.GetBytes(IV);\n            ICryptoTransform encryptor = aes.CreateEncryptor(aes.Key, aes.IV);\n            using (MemoryStream ms = new MemoryStream())\n            {\n                using (CryptoStream cs = new CryptoStream(ms, encryptor, CryptoStreamMode.Write))\n                {\n                    using (StreamWriter sw = new StreamWriter(cs))\n                    {\n                        sw.Write(plainText);\n                    }\n                }\n                return Convert.ToBase64String(ms.ToArray());\n            }\n        }\n    }\n\n    public static void WriteToRegFile(string filePath, string encryptedData)\n    {\n        string regContent = $"Windows Registry Editor Version 5.00\n\n[HKEY_CURRENT_USER\\Software\\YourApp]\n\"EncryptedData\"=\"{encryptedData}\"";\n        File.WriteAllText(filePath, regContent);\n    }\n}\n\n// Usage\nstring plainText = "Sensitive Data";\nstring encryptedData = RegistryEncryption.Encrypt(plainText);\nRegistryEncryption.WriteToRegFile("path_to_your_file.reg", encryptedData);