using System.Web;\nusing System.Web.Mvc;\n\npublic class ValidateIPAddressAttribute : ActionFilterAttribute\n{\n    private readonly string[] _blockedIPs;\n\n    public ValidateIPAddressAttribute(params string[] blockedIPs)\n    {\n        _blockedIPs = blockedIPs;\n    }\n\n    public override void OnActionExecuting(ActionExecutingContext filterContext)\n    {\n        var request = filterContext.HttpContext.Request;\n        var userIP = request.UserHostAddress;\n\n        if (_blockedIPs.Contains(userIP))\n        {\n            filterContext.Result = new HttpStatusCodeResult(403, "Forbidden");\n            return;\n        }\n\n        base.OnActionExecuting(filterContext);\n    }\n}\n\n// Applying the attribute to a controller or action\npublic class FileUploadController : Controller\n{\n    [ValidateIPAddress("192.168.1.1", "10.0.0.1")] // Example IP addresses to block\n    [HttpPost]\n    public ActionResult Upload(HttpPostedFileBase file)\n    {\n        if (file != null && file.ContentLength > 0)\n        {\n            // Handle file upload\n        }\n        \n        return View();\n    }\n}