public partial class MainPage : UserControl\n{\n    public MainPage()\n    {\n        InitializeComponent();\n        var viewModel = new MyViewModel();\n        this.DataContext = viewModel;\n        viewModel.PropertyChanged += ViewModel_PropertyChanged;\n    }\n\n    private void ViewModel_PropertyChanged(object sender, PropertyChangedEventArgs e)\n    {\n        if (e.PropertyName == "AllTeamsVisibility")\n        {\n            var viewModel = sender as MyViewModel;\n            if (viewModel != null)\n            {\n                // Assuming your DataGrid is named dataGrid and the column is at index 0\n                var column = dataGrid.Columns[0];\n                column.Visibility = viewModel.AllTeamsVisibility;\n            }\n        }\n    }\n}