using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a WMI event watcher to watch for process creation events\n        ManagementEventWatcher watcher = new ManagementEventWatcher(\n            new WqlEventQuery("SELECT * FROM Win32_ProcessStartTrace"));\n\n        // Attach an event handler to the event watcher\n        watcher.EventArrived += new EventArrivedEventHandler(ProcessStarted);\n\n        // Start listening for events\n        watcher.Start();\n\n        // Keep the application running\n        Console.WriteLine("Listening for process creation events. Press any key to exit...");\n        Console.ReadKey();\n\n        // Stop the event watcher\n        watcher.Stop();\n    }\n\n    private static void ProcessStarted(object sender, EventArrivedEventArgs e)\n    {\n        // Get the process name from the event\n        string processName = e.NewEvent.Properties["ProcessName"].Value.ToString();\n\n        // List of processes to search for\n        string[] listOfProcesses = { "notepad.exe", "calc.exe" }; // Example process names\n\n        // Check if the created process is in the list\n        foreach (string pName in listOfProcesses)\n        {\n            if (processName.Equals(pName, StringComparison.CurrentCultureIgnoreCase))\n            {\n                // Do some stuff\n                Console.WriteLine($"Process started: {processName}");\n            }\n        }\n    }\n}