using System;\nusing System.Runtime.InteropServices;\n\n[StructLayout(LayoutKind.Sequential)]\nstruct MyStruct\n{\n    public int Field1;\n    public IntPtr Field2; // Pointer size will differ between 32-bit and 64-bit\n}\n\nclass Program\n{\n    static void Main()\n    {\n        MyStruct myStruct = new MyStruct();\n        Console.WriteLine($"Size of MyStruct: {Marshal.SizeOf(myStruct)} bytes");\n    }\n}