using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string appDataPath = Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData);\n        string appFolder = Path.Combine(appDataPath, "Foo");\n        \n        // Ensure the directory exists\n        Directory.CreateDirectory(appFolder);\n\n        string configFilePath = Path.Combine(appFolder, "config.txt");\n\n        // Write to the configuration file\n        File.WriteAllText(configFilePath, "User last selected configuration info");\n\n        Console.WriteLine("Configuration saved to: " + configFilePath);\n    }\n}