using System;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private Button openModalButton;\n    private Button openSecondaryFormButton;\n    private SecondaryForm secondaryForm;\n\n    public MainForm()\n    {\n        openModalButton = new Button { Text = "Open Modal Dialog", Left = 10, Top = 10, Width = 150 };\n        openSecondaryFormButton = new Button { Text = "Open Secondary Form", Left = 10, Top = 40, Width = 150 };\n\n        openModalButton.Click += OpenModalButton_Click;\n        openSecondaryFormButton.Click += OpenSecondaryFormButton_Click;\n\n        Controls.Add(openModalButton);\n        Controls.Add(openSecondaryFormButton);\n    }\n\n    private void OpenModalButton_Click(object sender, EventArgs e)\n    {\n        using (var modalDialog = new ModalDialog())\n        {\n            modalDialog.ShowDialog(this);\n        }\n    }\n\n    private void OpenSecondaryFormButton_Click(object sender, EventArgs e)\n    {\n        if (secondaryForm == null || secondaryForm.IsDisposed)\n        {\n            secondaryForm = new SecondaryForm();\n            secondaryForm.Show();\n        }\n        else\n        {\n            secondaryForm.BringToFront();\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MainForm());\n    }\n}\n\npublic class ModalDialog : Form\n{\n    public ModalDialog()\n    {\n        Text = "Modal Dialog";\n        Width = 300;\n        Height = 200;\n    }\n}\n\npublic class SecondaryForm : Form\n{\n    public SecondaryForm()\n    {\n        Text = "Secondary Form";\n        Width = 300;\n        Height = 200;\n    }\n}