using System.Web.Mvc;\nusing System.Web.Security;\n\npublic class AccountController : Controller\n{\n    [AllowAnonymous]\n    public ActionResult CreateAdmin()\n    {\n        // Check if the admin user already exists\n        if (Membership.GetUser("admin") == null)\n        {\n            // Create the admin user\n            MembershipCreateStatus createStatus;\n            MembershipUser newUser = Membership.CreateUser("admin", "adminPassword123!", "admin@example.com", null, null, true, null, out createStatus);\n\n            if (createStatus == MembershipCreateStatus.Success)\n            {\n                // Add the user to the "Administrator" role\n                Roles.AddUserToRole("admin", "Administrator");\n                return Content("Admin user created successfully.");\n            }\n            else\n            {\n                return Content("Failed to create admin user: " + createStatus.ToString());\n            }\n        }\n        else\n        {\n            return Content("Admin user already exists.");\n        }\n    }\n}