using Microsoft.AspNetCore.Mvc;\nusing System.Collections.Generic;\nusing System.IO;\n\n[ApiController]\n[Route("api/[controller]")]\npublic class PdfController : ControllerBase\n{\n    [HttpPost("generate")]\n    public IActionResult GeneratePdf([FromBody] Dictionary<string, string> formData)\n    {\n        string templatePath = "path/to/template.pdf";\n        string outputPath = "path/to/output.pdf";\n\n        PdfService pdfService = new PdfService();\n        pdfService.GeneratePdf(templatePath, outputPath, formData);\n\n        byte[] fileBytes = System.IO.File.ReadAllBytes(outputPath);\n        return File(fileBytes, "application/pdf", "output.pdf");\n    }\n}