using System;\nusing System.Data;\nusing System.Data.Common;\nusing System.Data.SqlClient;\n\npublic class DatabaseOwner\n{\n    public static string GetDatabaseOwner(string connectionString)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            DataTable schema = connection.GetSchema("Databases");\n            \n            foreach (DataRow row in schema.Rows)\n            {\n                if (row["database_name"].ToString() == connection.Database)\n                {\n                    return row["owner"].ToString();\n                }\n            }\n        }\n        return null;\n    }\n    \n    public static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string owner = GetDatabaseOwner(connectionString);\n        \n        if (owner != null)\n        {\n            Console.WriteLine("The owner of the database is: " + owner);\n        }\n        else\n        {\n            Console.WriteLine("Database owner not found.");\n        }\n    }\n}