using System;\nusing System.Windows.Forms;\n\nnamespace FloatValidation\n{\n    public partial class Form1 : Form\n    {\n        public Form1()\n        {\n            InitializeComponent();\n\n            // Assuming textBox1 is your TextBox control\n            textBox1.TextChanged += TextBox1_TextChanged;\n        }\n\n        private void TextBox1_TextChanged(object sender, EventArgs e)\n        {\n            if (float.TryParse(textBox1.Text, out _))\n            {\n                textBox1.BackColor = System.Drawing.Color.White; // Valid input\n            }\n            else\n            {\n                textBox1.BackColor = System.Drawing.Color.LightCoral; // Invalid input\n            }\n        }\n    }\n}