protected void grdObitList_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        HyperLink hl = (HyperLink)e.Row.FindControl("HyperLink1");\n        if (hl != null)\n        {\n            // Assuming that obit_id is a data field in your data source\n            string obitId = DataBinder.Eval(e.Row.DataItem, "obit_id").ToString();\n\n            // Get the current page index (PageIndex is zero-based, so add 1 if you need a 1-based number)\n            int currentPage = grdObitList.PageIndex + 1;\n\n            // Construct the URL with the page number\n            hl.NavigateUrl = string.Format("obitDisplay.aspx?oid={0}&page={1}", obitId, currentPage);\n        }\n    }\n}